import React, { CSSProperties } from "react";
import { List, Image } from "antd-mobile";
import { List as VirtualizedList, AutoSizer } from "react-virtualized";

const item = {
  avatar:
    "https://images.unsplash.com/photo-1548532928-b34e3be62fc6?ixlib=rb-1.2.1&q=80&fm=jpg&crop=faces&fit=crop&h=200&w=200&ixid=eyJhcHBfaWQiOjE3Nzg0fQ",
  name: "Multi-Coin Wallet",
};
const eth = {
  avatar: "../assets/CryptoIcons/eth.png",
  name: "eth",
};
const btc = {
  avatar: "../assets/CryptoIcons/btc.png",
  name: "btc",
};
const nano = {
  avatar: "../assets/CryptoIcons/nano.png",
  name: "nano",
};
const theta = {
  avatar: "../assets/CryptoIcons/theta.png",
  name: "theta",
};
const xrp = {
  avatar: "../assets/CryptoIcons/xrp.png",
  name: "xrp",
};

const data = [item];
const data2 = [eth, btc, nano, theta, xrp];

export default () => {
  const getRowRenderer = (judge) => {
    const dataToUse = judge ? data : data2;
    return function rowRenderer({
      index,
      key,
      style,
    }: {
      index: number;
      key: string;
      style: CSSProperties;
    }) {
      const item = dataToUse[index];
      return (
        <List.Item
          key={key}
          style={style}
          prefix={
            <Image
              src={item.avatar}
              style={{ borderRadius: 20 }}
              fit="cover"
              width={40}
              height={40}
            />
          }
        >
          {item.name} {index}
        </List.Item>
      );
    };
  };

  return (
    <view>
      <List header="Multi-chain Wallet">
        <AutoSizer disableHeight>
          {({ width }: { width: number }) => (
            <VirtualizedList
              rowCount={1}
              rowRenderer={getRowRenderer(true)}
              width={width}
              height={60}
              rowHeight={60}
              overscanRowCount={10}
            />
          )}
        </AutoSizer>
      </List>
      <List header="Single-Coin Wallet">
        <AutoSizer disableHeight>
          {({ width }: { width: number }) => (
            <VirtualizedList
              rowCount={5}
              rowRenderer={getRowRenderer(false)}
              width={width}
              height={480}
              rowHeight={60}
              overscanRowCount={10}
            />
          )}
        </AutoSizer>
      </List>
    </view>
  );
};
