import React, { useState } from "react";
import { View, TextInput, SafeAreaView, StyleSheet } from "react-native";

const ImportMultiCoinWallet = () => {
  const [seed, onChangeSeed] = useState("");
  return (
    <SafeAreaView style={styles.mainWrapper}>
      <View>
        <TextInput
          value={""}
          numberOfLines={3}
          style={[styles.seedPhrase, styles.inputWidth]}
          secureTextEntry
          placeholder="Input your secret phrase, separate each word with space"
          blurOnSubmit
          returnKeyType="next"
          autoCapitalize="none"
          autoCorrect={false}
          onChange={onChangeSeed}
          value={seed}
        />
      </View>
    </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  inputWidth: { width: "99%" },
  mainWrapper: {
    flex: 1,
  },
  wrapper: {
    flex: 1,
    paddingHorizontal: 32,
  },
  title: {
    fontSize: 25,
    marginTop: 20,
    marginBottom: 20,
    // color: colors.text.default,
    justifyContent: "center",
    textAlign: "center",
    fontFamily: "EuclidCircularB-Bold",
    fontWeight: "600",
  },
  field: {
    marginVertical: 5,
    position: "relative",
  },
  fieldRow: {
    flexDirection: "row",
    alignItems: "flex-end",
  },
  fieldCol: {
    width: "70%",
  },
  fieldColRight: {
    flexDirection: "row-reverse",
    width: "30%",
  },
  label: {
    // color: colors.text.default,
    fontSize: 16,
    marginBottom: 12,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  ctaWrapper: {
    marginTop: 20,
  },
  errorMsg: {
    // color: colors.error.default,
    textAlign: "center",
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  seedPhrase: {
    marginBottom: 10,
    paddingTop: 20,
    paddingBottom: 20,
    paddingHorizontal: 20,
    fontSize: 20,
    borderRadius: 10,
    minHeight: 110,
    height: "auto",
    borderWidth: 1,
    // borderColor: colors.border.default,
    // backgroundColor: colors.background.default,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
    // color: colors.text.default,
  },
  padding: {
    paddingRight: 46,
  },
  biometrics: {
    alignItems: "flex-start",
    marginTop: 10,
  },
  biometryLabel: {
    flex: 1,
    fontSize: 16,
    // color: colors.text.default,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  biometrySwitch: {
    marginTop: 10,
    flex: 0,
  },
  termsAndConditions: {
    paddingVertical: 10,
  },
  passwordStrengthLabel: {
    height: 20,
    fontSize: 15,
    // color: colors.text.default,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  // eslint-disable-next-line react-native/no-unused-styles
  strength_weak: {
    // color: colors.error.default,
  },
  // eslint-disable-next-line react-native/no-unused-styles
  strength_good: {
    // color: colors.primary.default,
  },
  // eslint-disable-next-line react-native/no-unused-styles
  strength_strong: {
    // color: colors.success.default,
  },
  showMatchingPasswords: {
    position: "absolute",
    top: 52,
    right: 17,
    alignSelf: "flex-end",
  },
  qrCode: {
    marginRight: 10,
    borderWidth: 1,
    borderRadius: 6,
    // borderColor: colors.text.muted,
    paddingVertical: 4,
    paddingHorizontal: 6,
    marginTop: -50,
    marginBottom: 30,
    alignSelf: "flex-end",
  },
  inputFocused: {
    // borderColor: colors.primary.default,
    borderWidth: 2,
  },
  input: {
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
    fontSize: 16,
    paddingTop: 2,
    // color: colors.text.default,
  },
});
export default ImportMultiCoinWallet;
