import React, { useState } from "react";
import { View, TextInput, StyleSheet } from "react-native";

const ImportMultiCoinWallet = ({ navigation }: any) => {
  const arraysAreIdentical = (arr1, arr2) => {
    if (arr1.length !== arr2.length) return false;
    for (let i = 0, len = arr1.length; i < len; i++) {
      if (arr1[i] !== arr2[i]) {
        return false;
      }
    }
    return true;
  };
  const [seed, onChangeSeed] = useState("");
  const onHandleClick = () => {
    if (
      arraysAreIdentical(
        seed
          .split(/(\s+)/)
          .filter(function (e) {
            return e.trim().length > 0;
          })
          .map((element) => {
            return element.toUpperCase();
          }),
        [
          "Mouse",
          "Believe",
          "Walnut",
          "Slave",
          "Sporadically",
          "Family",
          "Skin",
          "Lobster",
          "Friend",
          "Intact",
          "Twenty",
          "Camera",
        ].map((element) => {
          return element.toUpperCase();
        })
      )
    ) {
      navigation.navigate("CreateStepThree");
    }
  };
  return (
    <View style={styles.paginationWrapper}>
      <TextInput
        value={seed}
        multiline={true}
        numberOfLines={3}
        style={styles.seedPhrase}
        placeholder="Input your secret phrase, separate each word with space"
        blurOnSubmit
        returnKeyType="next"
        autoCapitalize="none"
        autoCorrect={false}
        onChangeText={onChangeSeed}
      />
      <button
        style={{
          width: 320,
          height: 40,
          borderWidth: 1,
          borderColor: "#000",
          borderRadius: 18,
          marginTop: 20,
          alignItems: "center",
          justifyContent: "center",
        }}
        onClick={onHandleClick}
      >
        Submit
      </button>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  description: {
    width: 300,
    textAlign: "center",
    fontWeight: "500",
    marginBottom: 10,
    fontSize: 15,
  },
  value: {
    marginBottom: 10,
    color: "#FF4A8D",
    fontWeight: "500",
  },
  displayItemContainer: {
    margin: 7,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  displayButton: {
    flexDirection: "column",
    width: 100,
    height: 30,
    borderWidth: 1,
    borderColor: "#000",
    borderRadius: 13,
    marginRight: 6,
    alignItems: "center",
    justifyContent: "center",
    borderStyle: "dashed",
  },
  displaySelectedButton: {
    flexDirection: "column",
    width: 100,
    height: 30,
    borderWidth: 1,
    borderColor: "#7057FF",
    borderRadius: 13,
    marginRight: 6,
    alignItems: "center",
    justifyContent: "center",
    borderStyle: "solid",
  },
});
export default ImportMultiCoinWallet;
