import React, { useState } from "react";
import { View, TextInput, SafeAreaView, StyleSheet } from "react-native";

const ImportMultiCoinWallet = ({ navigation }: any) => {
  const arraysAreIdentical = (arr1, arr2) => {
    if (arr1.length !== arr2.length) return false;
    for (let i = 0, len = arr1.length; i < len; i++) {
      if (arr1[i] !== arr2[i]) {
        return false;
      }
    }
    return true;
  };
  const [seed, onChangeSeed] = useState("");
  const onHandleClick = () => {
    if (
      arraysAreIdentical(
        seed
          .split(/(\s+)/)
          .filter(function (e) {
            return e.trim().length > 0;
          })
          .map((element) => {
            return element.toUpperCase();
          }),
        [
          "Mouse",
          "Believe",
          "Walnut",
          "Slave",
          "Sporadically",
          "Family",
          "Skin",
          "Lobster",
          "Friend",
          "Intact",
          "Twenty",
          "Camera",
        ].map((element) => {
          return element.toUpperCase();
        })
      )
    ) {
      navigation.navigate("CreateStepThree");
    }
  };
  return (
    <View style={styles.paginationWrapper}>
      <TextInput
        value={seed}
        multiline={true}
        numberOfLines={3}
        style={[styles.seedPhrase, styles.inputWidth]}
        placeholder="Input your secret phrase, separate each word with space"
        blurOnSubmit
        returnKeyType="next"
        autoCapitalize="none"
        autoCorrect={false}
        onChangeText={onChangeSeed}
      />
      <button
        style={{
          width: 320,
          height: 40,
          borderWidth: 1,
          borderColor: "#000",
          borderRadius: 18,
          marginTop: 20,
          alignItems: "center",
          justifyContent: "center",
        }}
        onClick={onHandleClick}
      >
        Submit
      </button>
    </View>
  );
};
const styles = StyleSheet.create({
  paginationWrapper: {
    justifyContent: "space-between",
    flexDirection: "row",
    top: 50,
    position: "absolute",
    left: 0,
    right: 0,
    justifyContent: "center",
    alignItems: "center",
  },
  inputWidth: { width: "80%" },
  seedPhrase: {
    marginBottom: 10,
    paddingTop: 20,
    paddingBottom: 20,
    paddingHorizontal: 20,
    fontSize: 20,
    borderRadius: 10,
    minHeight: 110,
    height: "auto",
    borderWidth: 1,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  mainWrapper: {
    flex: 1,
  },
  wrapper: {
    flex: 1,
    paddingHorizontal: 32,
  },
  title: {
    fontSize: 25,
    marginTop: 20,
    marginBottom: 20,
    // color: colors.text.default,
    justifyContent: "center",
    textAlign: "center",
    fontFamily: "EuclidCircularB-Bold",
    fontWeight: "600",
  },
  field: {
    marginVertical: 5,
    position: "relative",
  },
  fieldRow: {
    flexDirection: "row",
    alignItems: "flex-end",
  },
  fieldCol: {
    width: "70%",
  },
  fieldColRight: {
    flexDirection: "row-reverse",
    width: "30%",
  },
  label: {
    // color: colors.text.default,
    fontSize: 16,
    marginBottom: 12,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  ctaWrapper: {
    marginTop: 20,
  },
  errorMsg: {
    // color: colors.error.default,
    textAlign: "center",
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  padding: {
    paddingRight: 46,
  },
  biometrics: {
    alignItems: "flex-start",
    marginTop: 10,
  },
  biometryLabel: {
    flex: 1,
    fontSize: 16,
    // color: colors.text.default,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  biometrySwitch: {
    marginTop: 10,
    flex: 0,
  },
  termsAndConditions: {
    paddingVertical: 10,
  },
  passwordStrengthLabel: {
    height: 20,
    fontSize: 15,
    // color: colors.text.default,
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
  },
  // eslint-disable-next-line react-native/no-unused-styles
  strength_weak: {
    // color: colors.error.default,
  },
  // eslint-disable-next-line react-native/no-unused-styles
  strength_good: {
    // color: colors.primary.default,
  },
  // eslint-disable-next-line react-native/no-unused-styles
  strength_strong: {
    // color: colors.success.default,
  },
  showMatchingPasswords: {
    position: "absolute",
    top: 52,
    right: 17,
    alignSelf: "flex-end",
  },
  qrCode: {
    marginRight: 10,
    borderWidth: 1,
    borderRadius: 6,
    // borderColor: colors.text.muted,
    paddingVertical: 4,
    paddingHorizontal: 6,
    marginTop: -50,
    marginBottom: 30,
    alignSelf: "flex-end",
  },
  inputFocused: {
    // borderColor: colors.primary.default,
    borderWidth: 2,
  },
  input: {
    fontFamily: "EuclidCircularB-Regular",
    fontWeight: "400",
    fontSize: 16,
    paddingTop: 2,
    // color: colors.text.default,
  },
});
export default ImportMultiCoinWallet;
