} from "react-native";
import { COLORS, SIZES, FONTS } from "../constants/theme";
import _ from "lodash";

const Withdraw = ({ route }) => {
  const [transactionFee, setTransactionFee] = useState("");
  const [transactionFeeAud, setTransactionFeeAud] = useState("");
  const onTransToAud = (currency) => {
    console.log(currency);
    if (currency == "BTC") {
      let temp: number = +transactionFee;
      temp = temp * 1046.48;
      setTransactionFeeAud(String(temp));
      console.log(transactionFeeAud);
    }
  };
  // const onIntChange = (e, method) => {
