  //   }
  //   if (valid) method(value);
  // };

  const { currencyItem } = route.params;

  return (
    <View
      style={{
        marginTop: SIZES.padding,
        marginHorizontal: SIZES.padding,
        padding: SIZES.padding,
        borderRadius: SIZES.radius,
        backgroundColor: COLORS.white,
        ...styles.shadow,
      }}
    >
      <Form layout="horizontal">
        <View>
          <Form.Item
            extra={
              <div>
                <a>PASTE</a>
              </div>
            }
          >
            <Input placeholder="Recipient Address" clearable />
          </Form.Item>
        </View>
        <View>
          <Form.Item
            extra={
              <div>
                <a style={{ marginRight: 20 }}>MAX </a>
                <a>{currencyItem}</a>
              </div>
            }
          >
            <Input placeholder="Amount" clearable />
          </Form.Item>
        </View>
        <View>
          <Form.Item
            extra={
              <div>
                <a onClick={(currencyItem) => onTransToAud(currencyItem)}>
                  {currencyItem}
                </a>
              </div>
            }
          >
            <Input
              placeholder="Transaction Fee"
              clearable
              type={"number"}
              onChange={(e) => onCryptoRateChange(e, setSenderNumber)}
              value={transactionFee}
            />
          </Form.Item>
        </View>
      </Form>
    </View>
  );
};

const styles = StyleSheet.create({
  shadow: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 4,
    },
  },
});

export default Withdraw;
