import React, { useState } from "react";
import { Form, Input } from "antd-mobile";
import {
  View,
  SafeAreaView,
  Text,
  Image,
  StyleSheet,
  StatusBar,
  TouchableOpacity,
  Dimensions,
  ScrollView,
  PixelRatio,
} from "react-native";
import { COLORS, SIZES, FONTS } from "../constants/theme";
import _ from "lodash";

const [transactionFee, setTransactionFee] = useState("");
const onIntChange = (e, method) => {
  let valid = true;
  const value = e.target.value;
  if (value) {
    const lastChar = value[value.length - 1];
    if (!(_.isInteger(parseInt(lastChar)) || lastChar === ".")) valid = false;
  }
  if (valid) method(value);
};

const Withdraw = ({ route }) => {
  const { currencyItem } = route.params;

  return (
    <View
      style={{
        marginTop: SIZES.padding,
        marginHorizontal: SIZES.padding,
        padding: SIZES.padding,
        borderRadius: SIZES.radius,
        backgroundColor: COLORS.white,
        ...styles.shadow,
      }}
    >
      <Form layout="horizontal">
        <View>
          <Form.Item
            extra={
              <div>
                <a>PASTE</a>
              </div>
            }
          >
            <Input placeholder="Recipient Address" clearable />
          </Form.Item>
        </View>
        <View>
          <Form.Item
            extra={
              <div>
                <a style={{ marginRight: 20 }}>MAX </a>
                <a>{currencyItem}</a>
              </div>
            }
          >
            <Input placeholder="Amount" clearable />
          </Form.Item>
        </View>
        <View>
          <Form.Item>
            <Input
              onChange={(e) => onIntChange(e, setTransactionFee)}
              value={transactionFee}
              placeholder="Transaction Fee"
            />
          </Form.Item>
        </View>
      </Form>
    </View>
  );
};

const styles = StyleSheet.create({
  shadow: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 4,
    },
  },
});

export default Withdraw;
