import React, { CSSProperties } from "react";
import { List, Image } from "antd-mobile";
import { List as VirtualizedList, AutoSizer } from "react-virtualized";
import multiCoinStatus from "../constants/newDummy";

const CoinList = ({ navigation }) => {
  const getRowRenderer = (judge) => {
    const navigationSelect = (item) => {
      if (item.name == "btc")
        return () => navigation.navigate("CreateStepThree");
      if (item.name == "eth")
        return () => navigation.navigate("CreateStepThree");
      if (item.name == "nano")
        return () => navigation.navigate("CreateStepThree");
      if (item.name == "theta")
        return () => navigation.navigate("CreateStepThree");
      if (item.name == "xrp")
        return () => navigation.navigate("CreateStepThree");
    };
    const dataToUse = multiCoinStatus;

    return function rowRenderer({
      index,
      key,
      style,
    }: {
      index: number;
      key: string;
      style: CSSProperties;
    }) {
      const item = dataToUse[index];
      return (
        <List.Item
          key={key}
          style={style}
          onClick={navigationSelect("Sendto", item.currency.toLowerCase())}
          prefix={
            <Image
              src={item.avatar}
              style={{ borderRadius: 20 }}
              fit="cover"
              width={40}
              height={40}
            />
          }
        >
          {item.description}
        </List.Item>
      );
    };
  };

  return (
    <view>
      <List>
        <AutoSizer disableHeight>
          {({ width }: { width: number }) => (
            <VirtualizedList
              rowCount={5}
              rowRenderer={getRowRenderer(false)}
              width={width}
              height={300}
              rowHeight={60}
              overscanRowCount={10}
            />
          )}
        </AutoSizer>
      </List>
    </view>
  );
};

export default CoinList;
