import React from "react";
import { Image, ScrollView, View } from "react-native";
import { List } from "@ant-design/react-native";

const SecretPhraseImport = ({ navigation }: any) => {
  const [agree, setAgree] = useState(false);

  return (
    <View style={styles.container}>
      <Image
        source={require("../assets/images/fireworkPlaceholder.jpg")}
        style={styles.imageStyle}
      />
      <View style={styles.listContainer}>
        <button
          disabled={agree}
          className="btn"
          onClick={() => navigation.navigate("CreateStepFour")}
        >
          Continue
        </button>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "space-between",
    marginBottom: 50,
  },
  listContainer: {
    flex: 1,
    alignItems: "center",
    justifyContent: "flex-end",
  },
  checkboxContainer: {
    flexDirection: "row",
    marginBottom: 20,
  },
  imageStyle: {
    height: PixelRatio.getPixelSizeForLayoutSize(135),
    width: "100%",
  },
  checkbox: {
    alignSelf: "center",
  },
  label: {
    margin: 8,
  },
  buttonContainer: {
    bottom: 100,
    flexDirection: "row",
    marginHorizontal: 24,
  },
  button: {
    flex: 1,
    paddingVertical: 20,
    marginHorizontal: 8,
    borderRadius: 24,
    backgroundColor: "#1cb278",
  },
  buttonText: {
    color: "white",
    fontWeight: "600",
    textAlign: "center",
  },
});

export default SecretPhraseImport;
