import { useState } from "react";
import { View, TextInput, SafeAreaView, StyleSheet } from "react-native";
import { Row, Image } from "antd-mobile";

const ImportMultiCoinWallet = () => {
  const [content, setContent] = useState("");

  const onIntChange = (e, method) => {
    let valid = true;
    const value = e.target.value;
    if (value) {
      const lastChar = value[value.length - 1];
      if (!(_.isInteger(parseInt(lastChar)) || lastChar === "+")) valid = false;
    }
    if (valid) method(value);
  };

  const onClick = async () => {
    await props.onSubmit({
      content,
      senderNumber,
      contactName,
      contactEmail,
      contactMobile,
    });
    setContent("");
    setSenderNumber("");
    setContactName("");
    setContactEmail("");
    setContactMobile("");
  };

  return (
    <React.Fragment>
      <View>
        <View flex="auto">
          <h4>Report your suspicions SMS message:</h4>
        </View>
      </View>
      <Row>
        <View flex="450px">
          <Input.TextArea
            allowClear
            autoSize={{ minRows: 6, maxRows: 6 }}
            onChange={(e) => setContent(e.target.value)}
            value={content}
          />
        </View>
      </Row>
      <Button disabled={!content} onClick={onClick} type="primary">
        Submit
      </Button>
    </React.Fragment>
  );
};
