import { useState } from "react";
import * as React from "react";
import { View, Button, Text, StyleSheet, Image } from "react-native";

const CreateStepThree = ({ navigation }: any) => {
  const [agree, setAgree] = useState(false);

  // When the button is clicked
  const btnHandler = () => {
    alert("The button is clickable!");
  };

  return (
    <View style={styles.container}>
      <View style={styles.listContainer}>
        <Image
          source={require("../assets/images/A coin.jpeg")}
          style={styles.imageStyle}
        />
        <button
          disabled={agree}
          className="btn"
          onClick={() => navigation.navigate("CreateStepFour")}
        >
          Continue
        </button>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "space-between",
    marginBottom: 50,
  },
  listContainer: {
    flex: 1,
    alignItems: "center",
    justifyContent: "flex-end",
  },
  checkboxContainer: {
    flexDirection: "row",
    marginBottom: 20,
  },
  checkbox: {
    alignSelf: "center",
  },
  label: {
    margin: 8,
  },
  buttonContainer: {
    bottom: 100,
    flexDirection: "row",
    marginHorizontal: 24,
  },
  button: {
    flex: 1,
    paddingVertical: 20,
    marginHorizontal: 8,
    borderRadius: 24,
    backgroundColor: "#1cb278",
  },
  buttonText: {
    color: "white",
    fontWeight: "600",
    textAlign: "center",
  },
});

export default CreateStepThree;
