import React, { CSSProperties } from "react";
import { List, Image } from "antd-mobile";
import { List as VirtualizedList, AutoSizer } from "react-virtualized";
import newDummy from "../constants/newDummy";
import { View } from "react-native";

const CoinList = ({ navigation }) => {
  const dataToUse = newDummy.multiCoinStatus;
  const getRowRenderer = () => {
    const navigationSelect = (item) => {
      if (item == "btc")
        return () =>
          navigation.navigate("Send To", {
            currencyItem: dataToUse[0].currency,
          });
      if (item == "eth")
        return () =>
          navigation.navigate("Send To", {
            currencyItem: dataToUse[1].currency,
          });
      if (item == "nano")
        return () =>
          navigation.navigate("Send To", {
            currencyItem: dataToUse[2].currency,
          });
      if (item == "theta")
        return () =>
          navigation.navigate("Send To", {
            currencyItem: dataToUse[3].currency,
          });
      if (item == "xrp")
        return () =>
          navigation.navigate("Send To", {
            currencyItem: dataToUse[4].currency,
          });
    };

    return function rowRenderer({
      index,
      key,
      style,
    }: {
      index: number;
      key: string;
      style: CSSProperties;
    }) {
      const item = dataToUse[index];
      return (
        <List.Item
          key={key}
          style={style}
          // onClick={navigation.navigate("Send To", {
          //   currencyItem: item.currency,
          // })}
          onClick={navigationSelect(item.currency.toLowerCase())}
          prefix={
            <Image
              src={item.icon}
              style={{ borderRadius: 20 }}
              fit="cover"
              width={40}
              height={40}
            />
          }
        >
          {item.description}
        </List.Item>
      );
    };
  };

  return (
    <View>
      <List>
        <AutoSizer disableHeight>
          {({ width }: { width: number }) => (
            <VirtualizedList
              rowCount={5}
              rowRenderer={getRowRenderer()}
              width={width}
              height={300}
              rowHeight={60}
              overscanRowCount={10}
            />
          )}
        </AutoSizer>
      </List>
    </View>
  );
};

export default CoinList;
