import React, { useState } from "react";
import { Form, Input } from "antd-mobile";
import {
  View,
  SafeAreaView,
  Text,
  Image,
  StyleSheet,
  StatusBar,
  TouchableOpacity,
  Dimensions,
  ScrollView,
  PixelRatio,
} from "react-native";
import { COLORS, SIZES, FONTS } from "../constants/theme";
import _ from "lodash";
import { newDummyData } from "../index";

const Withdraw = ({ route }) => {
  const [transactionFee, setTransactionFee] = useState("");
  const [transactionFeeAud, setTransactionFeeAud] = useState("");
  const [SendCrypto, setSendCrypto] = useState("");
  const [SendAud, setSendAud] = useState("");
  const { currencyItem } = route.params;
  const [currentCurrency, setCurrentCurrency] = useState(currencyItem);
  const onTransToAud = (e) => {
    console.log(currencyItem);
    if (currentCurrency == "BTC") {
      let temp: number = +transactionFee;
      temp = temp * 1046.48;
      if (temp == 0) temp = null;
      if (e.toString() == transactionFee) {
        setTransactionFeeAud(String(temp));
        setCurrentCurrency("AUD");
      }
      if (e.toString() == SendCrypto) {
        setTransactionFeeAud(String(temp));
        setCurrentCurrency("AUD");
      }
    }
  };
  const onTransToCrypto = () => {
    console.log(currencyItem);
    if (currentCurrency == "AUD") {
      let temp: number = +transactionFeeAud;
      temp = temp / 1046.48;
      if (temp == 0) temp = null;
      setTransactionFee(String(temp));
      setCurrentCurrency(currencyItem);
    }
  };

  return (
    <View
      style={{
        marginTop: SIZES.padding,
        marginHorizontal: SIZES.padding,
        padding: SIZES.padding,
        borderRadius: SIZES.radius,
        backgroundColor: COLORS.white,
        ...styles.shadow,
      }}
    >
      <Form layout="horizontal">
        <View>
          <Form.Item
            extra={
              <div>
                <a>PASTE</a>
              </div>
            }
          >
            <Input placeholder="Recipient Address" clearable />
          </Form.Item>
        </View>
        <View>
          <Form.Item
            extra={
              <div>
                <a style={{ marginRight: 20 }}>MAX </a>
                <a>{currentCurrency}</a>
              </div>
            }
          >
            <Input placeholder="Amount" clearable />
          </Form.Item>
        </View>
        {currentCurrency != "AUD" && (
          <View>
            <Form.Item
              extra={
                <div>
                  <a onClick={onTransToAud}>{currentCurrency}</a>
                </div>
              }
            >
              <Input
                placeholder="Transaction Fee"
                clearable
                type={"number"}
                onChange={(e) => setTransactionFee(e)}
                value={transactionFee}
              />
            </Form.Item>
          </View>
        )}
        {currentCurrency == "AUD" && (
          <View>
            <Form.Item
              extra={
                <div>
                  <a onClick={onTransToCrypto}>AUD</a>
                </div>
              }
            >
              <Input
                placeholder="Transaction Fee"
                clearable
                type={"number"}
                onChange={(e) => setTransactionFeeAud(e)}
                value={transactionFeeAud}
              />
            </Form.Item>
          </View>
        )}
      </Form>
    </View>
  );
};

const styles = StyleSheet.create({
  shadow: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 4,
    },
  },
});

export default Withdraw;
