import React, { useContext, useState } from "react";
import {
  SafeAreaView,
  Text,
  FlatList,
  View,
  TouchableOpacity,
} from "react-native";
import shortid from "shortid";
import Feather from "react-native-vector-icons/Feather";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import Header from "../components/Header";
import MailInfo from "../components/MailInfo";
import BottomTab from "../components/Header";
import AuthContext from "../components/AuthContext";
import getScreenParent from "../components/ScreenParent";
import combineData from "../components/dataHelper";

const MailList = ({ navigation, route }) => {
  const screenTitle = route.params?.screenTitle;

  const state = {
    username: "Princewill Iroka",
    mails: [
      {
        headers: {
          subject:
            "What You Need to Know About React 18 | Sheeraz Shaikh in JavaScript in Plain English",
          date: "June 14",
          from: "Medium Daily Digest",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo:
            "https://cdn4.iconfinder.com/data/icons/social-media-circle-7/512/Medium_circle-512.png",
        },
        body: "Here is a round-up of our stories for the month of June",
        status: "read",
      },
      {
        headers: {
          subject: "John Doe commented on your post",
          date: "May 15",
          from: "LinkedIn",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo:
            "https://cdn4.iconfinder.com/data/icons/social-messaging-ui-color-shapes-2-free/128/social-linkedin-circle-512.png",
        },
        body: "Princewill Iroka here are your most recent notifications",
        status: "read",
      },
      {
        headers: {
          subject: "Your Cloud Firestore database rules need to be updated",
          date: "May 10",
          from: "firebase-noreply",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo: "",
        },
        body: `Analysis is run daily. If you've modified your rules in the last 24 hours that may not be accounted for.`,
        status: "unread",
      },
      {
        headers: {
          subject: `One week until Vercel's Next.js Conf`,
          date: "May 8",
          from: "Vercel",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo: "",
        },
        body: `Join the Next.js Conf on June 15th for a special edition, presented by Vercel and Google Chrome, and including some incredible announcements and new features!`,
        status: "unread",
      },
      {
        headers: {
          subject: `New jobs that match your saved search on Stack Overflow`,
          date: "May 8",
          from: "Stack Overflow",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo:
            "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ef/Stack_Overflow_icon.svg/768px-Stack_Overflow_icon.svg.png",
        },
        body: `New jobs that match your saved search on Stack Overflow`,
        status: "read",
      },
      {
        headers: {
          subject: `New jobs that match your saved search on Stack Overflow`,
          date: "May 7",
          from: "Udacity",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo:
            "https://upload.wikimedia.org/wikipedia/commons/3/3b/Udacity_logo.png",
        },
        body: `New jobs that match your saved search on Stack Overflow`,
        status: "unread",
      },
      {
        headers: {
          subject: `Learn Mobile UI/UX from Nike & Adobe Designer Jesse Showalter`,
          date: "May 3",
          from: "Dribbble",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo: "",
        },
        body: `Level-up your mobile app & interface skills in our new Dribbble Workshop`,
        status: "read",
      },
      {
        headers: {
          subject: `The Ultimate Guide to MERN Stack" + 5 more trending DEV Community posts`,
          date: "May 3",
          from: "DEV Community Digest",
          to: "user@email.com",
          sender: "user@email.com",
          senderLogo: "https://d2fltix0v2e0sb.cloudfront.net/dev-black.png",
        },
        body: `Recent posts you might find valuable based on your interests ❤️`,
        status: "unread",
      },
    ],
  };
  const [data, setData] = useState({
    contentOffsetY: "",
    isScrollingUp: false,
  });

  const handleDrawer = () => {
    navigation?.openDrawer();
  };

  const handleNavigation = (screen, params) => {
    navigation.navigate(getScreenParent(screen), screen, params);
  };

  const onScroll = ({ contentOffset }) => {
    if (contentOffset.y > 5) {
      const existingContentOffsetY = data.contentOffsetY;
      const isScrollingUp = existingContentOffsetY > contentOffset.y;
      setData(
        combineData(data, { contentOffsetY: contentOffset.y, isScrollingUp })
      );
    }
  };

  const getMails = () => {
    let mails,
      arr = ["Inbox", "All mail"];
    if (arr.includes(screenTitle)) {
      mails = state?.mails;
    } else {
      mails =
        state?.mails?.filter(
          (mail) => mail.status === screenTitle?.toLowerCase()
        ) || [];
    }
    return mails;
  };

  const renderMailInfo = ({ item }) => {
    return (
      <MailInfo
        mail={item}
        key={shortid.generate()}
        // navigateToRoute={(screen) =>
        //   handleNavigation(screen, { mail: item, screenTitle })
        // }
      />
    );
  };

  const ComposeButton = () => {
    const { isScrollingUp } = data;
    return (
      <TouchableOpacity
        style={[
          styles.composeButton,
          isScrollingUp
            ? styles.composeButtonRound
            : styles.composeButtonCurved,
        ]}
        // onPress={() => handleNavigation("Compose")}
      >
        <Feather
          name="edit-2"
          size={20}
          color="#D93025"
          style={styles.menuIcon}
        />
        {!isScrollingUp ? (
          // <TransitionText animationStyle="slideInRight">
          <Text style={styles.composeText}>Compose</Text>
        ) : // </TransitionText>
        null}
      </TouchableOpacity>
    );
  };

  const EmptyListComponent = () => {
    return (
      <View style={styles.emptyListContainer}>
        <MaterialCommunityIcons name="file" size={120} color="#f4a9a2" />
        <Text style={styles.emptyListText}>Nothing in screenTitle</Text>
      </View>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>
        <Header toggleDrawer={() => handleDrawer()} />
        {/*<Text style={styles.screenTitle}>{screenTitle}</Text>*/}
        <Text style={styles.screenTitle}>"screenTitle"</Text>
        {getMails().length > 0 ? (
          <FlatList
            data={getMails()}
            keyExtractor={(item, index) => shortid.generate()}
            renderItem={renderMailInfo}
            horizontal={false}
            showsVerticalScrollIndicator={false}
            style={styles.flatList}
            onScroll={({ nativeEvent }) => {
              onScroll(nativeEvent);
            }}
            scrollEventThrottle={400}
          />
        ) : (
          <EmptyListComponent />
        )}
      </View>
      <ComposeButton />
      {/*<BottomTab navigateToRoute={(screen) => handleNavigation(screen)} />*/}
      <BottomTab toggleDrawer={undefined} />
    </SafeAreaView>
  );
};
import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  content: {
    padding: 16,
    paddingBottom: 180,
  },
  screenTitle: {
    textTransform: "uppercase",
    opacity: 0.5,
    fontSize: 15,
    fontWeight: "bold",
    marginBottom: 10,
  },
  flatList: {
    paddingBottom: 120,
  },
  composeButton: {
    position: "absolute",
    bottom: 100,
    right: 25,
    zIndex: 2,
    shadowRadius: 2,
    shadowOffset: {
      width: 0,
      height: -3,
    },
    shadowColor: "#000000",
    elevation: 4,
    backgroundColor: "#fff",
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
  },
  composeButtonRound: {
    borderRadius: 50,
    width: 50,
    height: 50,
    justifyContent: "center",
  },
  composeButtonCurved: {
    borderRadius: 25,
    width: 150,
    height: 50,
    paddingHorizontal: 16,
    justifyContent: "space-between",
  },
  composeText: {
    fontSize: 17,
    color: "#D93025",
    fontWeight: "600",
  },
  emptyListContainer: {
    display: "flex",
    justifyContent: "center",
    height: "100%",
    alignItems: "center",
    paddingBottom: 100,
  },
  emptyListText: {
    textAlign: "center",
    fontSize: 19,
    opacity: 0.6,
    marginTop: 20,
  },
  menuIcon: {
    marginRight: 10,
  },
  mailDate: {
    fontSize: 12,
    marginBottom: "auto",
    opacity: 0.8,
  },
});

export default MailList;
