import React, { useState, useEffect } from "react";
import { View, Text, TouchableOpacity, Image, Dimensions } from "react-native";
import { StyleSheet } from "react-native";

const PhonePage = ({ route, navigation }) => {
  const [iconChoice, setIconChoice] = React.useState(true);
  useEffect(() => {
    const readOrNot = route.params.readOrNot;
    setIconChoice(readOrNot);
  }, []);
  // useEffect(() => {
  //   if (route.params?.readOrNot) {
  //     const readOrNot = route.params.readOrNot;
  //     console.log(readOrNot);
  //   } else {
  //     const readOrNot = false;
  //     console.log(readOrNot);
  //   }
  // });
  const { width, height } = Dimensions.get("window");
  return (
    <View>
      <View style={{ width: width, height: height }}>
        <Image
          style={styles.backgroundImage}
          source={require("../../assets/images/phone.png")}
        />
        {!iconChoice && (
          <TouchableOpacity
            style={styles.mail}
            onPress={() => navigation.navigate("Mail Page")}
          >
            <Image
              style={styles.mail}
              source={require("../../assets/images/mailWithOneUnread.png")}
            />
          </TouchableOpacity>
        )}
        {iconChoice && (
          <TouchableOpacity
            style={styles.mail}
            onPress={() => navigation.navigate("Mail Page")}
          >
            <Image
              style={styles.mail}
              source={require("../../assets/images/mailNoRead.png")}
            />
          </TouchableOpacity>
        )}
        <Text
          onPress={() => navigation.navigate("Mail Page")}
          style={styles.walletWord}
        >
          Wallet
        </Text>
      </View>
      <TouchableOpacity
        style={styles.market}
        onPress={() => navigation.navigate("Mail Page")}
      >
        <Image
          style={styles.mail}
          source={require("../../assets/images/coinWallet.png")}
        />
      </TouchableOpacity>
      <Text
        onPress={() => navigation.navigate("Mail Page")}
        style={styles.mailWord}
      >
        Mail
      </Text>
    </View>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    width: null,
    height: null,
    resizeMode: "stretch",
  },
  mail: {
    width: 50,
    height: 50,
    position: "absolute",
    bottom: "30%",
    left: "10%",
    resizeMode: "stretch",
  },
  walletWord: {
    width: 50,
    height: 50,
    position: "absolute",
    bottom: "27%",
    left: "27%",
    resizeMode: "stretch",
    color: "white",
  },
  mailWord: {
    width: 50,
    height: 50,
    position: "absolute",
    bottom: "27%",
    left: "13%",
    resizeMode: "stretch",
    color: "white",
  },
  market: {
    width: 50,
    height: 50,
    position: "absolute",
    bottom: "30%",
    left: "25%",
    resizeMode: "stretch",
  },
  container: {
    flex: 1,
  },
});
export default PhonePage;
